import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { Filter } from "../../containers/Filter/Filter"; // Importa el componente Filter
import "./Navbar.css";

function NavBar() {
  const [name, setName] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    setName("");
  }

  return (
    <div className="navBar">
      <div className="videogames">
        <Link to="/">
          <h3>Landing!</h3>
        </Link>
      </div>
      <div className="home">
        <Link to="/home">
          <h3>Home</h3>
        </Link>
      </div>
      <div className="searchbar">
        <form onSubmit={(e) => handleSubmit(e)}>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Search videogame..."
            type="text"
          ></input>
          <NavLink to={`/results/${name}`}>
            <button name="name" type="submit">
              Go!
            </button>
          </NavLink>
        </form>
      </div>
      <div className="create">
        <Link to="/create">
          <h3>Create</h3>
        </Link>
      </div>
      <div className="about">
        <Link to="/about">
          <h3>About</h3>
        </Link>
      </div>
      <Filter /> {/* Agrega el componente Filter aqu√≠ */}
    </div>
  );
}

export default NavBar;
