// Home.jsx
import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogames, resetAll } from "../../actions/index";
import Videogames from "../../components/Videogames/Videogames";
import { Pagination } from "../../components/Pagination/Pagination";
import { Filter } from "../Filter/Filter";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();

  const filteredVideogames = useSelector((state) => state.filteredVideogames);
  const filterBy = useSelector((state) => state.filterBy);
  const orderBy = useSelector((state) => state.orderBy);
  const videogames = useSelector((state) => state.videogames);

  // Nuevo estado para almacenar los videojuegos originales
  const [originalVideogames, setOriginalVideogames] = useState([]);

  useEffect(() => {
    dispatch(getVideogames()).then((data) => {
      // Almacena los videojuegos originales cuando se monta el componente
      setOriginalVideogames(data);
    });
  }, [dispatch]);

  // Utiliza los videojuegos originales si se selecciona "All" en el filtro de orden
  let allVideogames;
  if (filterBy === "All" && orderBy === "Select") {
    allVideogames = originalVideogames;
  } else {
    allVideogames = filteredVideogames;
  }

  function paginate(e, num) {
    e.preventDefault();
    setPage(num);
  }

  const [page, setPage] = useState(1);
  const [videogamesPerPage] = useState(15);

  let lastCardPerPage = page * videogamesPerPage;
  let firstCardPerPage = lastCardPerPage - videogamesPerPage;
  let currentPageGames = allVideogames.slice(firstCardPerPage, lastCardPerPage);

  return (
    <div className="home">
      <Filter paginate={paginate} />
      <Pagination
        videogamesPerPage={videogamesPerPage}
        totalVideogames={allVideogames.length}
        paginate={paginate}
      />
      <Videogames videogames={currentPageGames} />
      <Pagination
        videogamesPerPage={videogamesPerPage}
        totalVideogames={allVideogames.length}
        paginate={paginate}
      />
    </div>
  );
}
