import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getVideogameById } from "../../actions/index";
import NotFound from "../../components/NotFound/NotFound";
import "./GameDetail.css";

function GameDetail({ id }) {
  const dispatch = useDispatch();
  const videogame = useSelector((store) => store.searchVideogameById);

  useEffect(() => {
    // Enfocar al inicio de la p치gina cuando se monta el componente
    window.scrollTo(0, 0);

    dispatch(getVideogameById(id));
  }, [id]); // eslint-disable-line react-hooks/exhaustive-deps

  return (    
    <div className="full">
      {/* Bot칩n para volver arriba */}
      <Link to="/home">
        <button className="button" type="submit">游몾</button>
      </Link>

      <div className="info">
        <div className="image">
          {videogame.image === null || !videogame.image ? (
            <NotFound image={"noimage"} />
          ) : (
            <img src={videogame.image} alt={videogame.name} />
          )}
          <div>
            <h1>{videogame.name} </h1>
            <h5>({videogame.released})</h5>
          </div>
        </div>
        <div className="details">
          <div className="text">
            <h2>About this game:</h2>
            <p>{videogame.description}</p>
          </div>
          <div className="Genres">
            <div className="genres">
              It's an {videogame.genres} game ranked at {videogame.rating} points.
            </div>
          </div>
          <div className="Platforms">
            <div className="platforms">
              <p>Play it at {videogame.platforms}.</p>
            </div>
          </div>
        </div>
      </div>

      {/* Bot칩n para volver abajo */}
      <Link to="/home">
        <button className="button" type="submit">游몾</button>
      </Link>
    </div>    
  );
}

export default GameDetail;
