import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogames, resetAll } from "../../actions/index";
import Videogames from "../../components/Videogames/Videogames";
import { Pagination } from "../../components/Pagination/Pagination";
import { Filter } from "../Filter/Filter";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();

  const filteredVideogames = useSelector((state) => state.filteredVideogames);
  const filterBy = useSelector((state) => state.filterBy);
  const orderBy = useSelector((state) => state.orderBy);
  const videogames = useSelector((state) => state.videogames);

  useEffect(() => {
    dispatch(getVideogames());
  }, [dispatch]);

  // Estado local para almacenar los videojuegos actuales después de cualquier filtrado/ordenación
  const [currentVideogames, setCurrentVideogames] = useState(videogames);

  // Filtrado y Ordenado
  useEffect(() => {
    let allVideogames;
    if (filterBy === "All" && orderBy === "Select") {
      allVideogames = videogames;
    } else {
      // Aplicar filtros y ordenación según el estado actual
      // Puedes ajustar esta lógica según tus necesidades exactas
      // Aquí asumo que filterBy y orderBy son las únicas opciones para filtrar y ordenar
      // Si hay más opciones, debes ajustar la lógica aquí.
      allVideogames = applyFilterAndOrder(videogames, filterBy, orderBy);
    }

    // Actualizar el estado local con los videojuegos actuales
    setCurrentVideogames(allVideogames);
  }, [filterBy, orderBy, videogames]);

  function applyFilterAndOrder(data, filter, order) {
    // Lógica para aplicar filtros y ordenación
    // Debes ajustar esto según tus necesidades exactas
    // En este ejemplo, simplemente retorno el array sin cambios
    return data;
  }

  // Paginacion
  function paginate(e, num) {
    e.preventDefault();
    setPage(num);
  }

  const [page, setPage] = useState(1);
  const [videogamesPerPage] = useState(15);

  let lastCardPerPage = page * videogamesPerPage;
  let firstCardPerPage = lastCardPerPage - videogamesPerPage;
  let currentPageGames = currentVideogames.slice(firstCardPerPage, lastCardPerPage);

  return (
    <div className="home">
      <Filter paginate={paginate} />
      <Pagination
        videogamesPerPage={videogamesPerPage}
        totalVideogames={currentVideogames.length}
        paginate={paginate}
      />
      <Videogames videogames={currentPageGames} />
      <Pagination
        videogamesPerPage={videogamesPerPage}
        totalVideogames={currentVideogames.length}
        paginate={paginate}
      />
    </div>
  );
}
